<script>
import { throttle } from '../util/index'

export default {
  data () {
    return {
      config: {
        paddingX: 60,
        paddingY: 60,
        // 控制圆角   阴影尺寸位置  背景色  下载文件到本地
      }

    }
  },
  watch: {
    config: {
      deep: true,
      handler: function () {
        this.handler()
      }
    }
  },
  methods: {
    onClickCopy() {
      this.$bus.$emit('copy')
    },
    handler() {
      if (this.execHandler) {
        this.execHandler()
        return
      }
      this.execHandler = throttle(this.updateCanvas, 1000)
    },
    updateCanvas() {
      this.$bus.$emit('update-config', this.config)
    }
  }
}
</script>

